export const backGroundImage = (type: string) => {
  switch(type) {
    case 'grass': 
      return grass;
    case 'fire':
      return fire;
    case 'electric':
      return electric;
    case 'water':
      return water;
    case 'psychic':
      return psychic;
    case 'rock':
      return rock;
    case 'fighting':
      return fighting;
    case 'ghost':
      return ghost;
    case 'ground':
      return ground;
    case 'poison':
      return poison;
    case 'steel':
      return steel;
    case 'bug':
      return bug;
    case 'fairy':
      return fairy;
    case 'dark':
      return dark;
    case 'dragon':
      return dragon;
    case 'flying':
      return flying;
    case 'ice':
      return ice;
    default:
      return grass;
  }
}